/**
 * Test class for benchmarkSuite
 * Generated by PHPUnit on 2011-01-12 at 23:50:17.
 *
 * @package WebCheckerTests
 * @version //autogen//
 * @codeCoverageIgnore
 */
class checkerBenchmarkSuiteTest extends PHPUnit_Framework_TestCase
{

    /**
     * Sets up the fixture, for example, opens a network connection.
     * This method is called before a test is executed.
     *
     * @group WebCheckerTests
     */
    protected function setUp()
    {
        $this->object = new checkerBenchmarkSuite();
    }

    /**
     * Tears down the fixture, for example, closes a network connection.
     * This method is called after a test is executed.
     *
     * @group WebCheckerTests
     */
    protected function tearDown()
    {
    }

    /**
     * @group WebCheckerTests
     */
    public function testSet()
    {
        $this->object->suite = array();
    }

    /**
     * @group WebCheckerTests
     */
    public function testGet()
    {
        $this->object->suite = array();
        $foo = $this->object->suite;
    }

    /**
     * @expectedException ezcBasePropertyNotFoundException
     * @group WebCheckerTests
     */
    public function testGetException()
    {
        $foo = $this->object->bar;
    }

    /**
     * @expectedException ezcBaseValueException
     * @group WebCheckerTests
     */
    public function testSetException_1()
    {
        $this->object->suite = -1;
    }

    /**
     * @expectedException ezcBasePropertyNotFoundException
     * @group WebCheckerTests
     */
    public function testSetException_2()
    {
        $this->object->foo = 'bar';
    }

    /**
     * @group WebCheckerTests
     */
    public function testIsset()
    {
        $this->object->suite = array();
        $this->assertTrue(isset($this->object->suite));
    }

    /**
     * @group WebCheckerTests
     */
    public function testToString()
    {
        (string)$foo = (string)$this->object;
    }

    /**
     * @expectedException ezcBaseValueException
     * @group WebCheckerTests
     */
    public function testAddException()
    {
        $benchmark = new self();
        $result = $this->object->add( $benchmark );
    }

    /**
     * @group WebCheckerTests
     */
    public function testAdd_NoKey()
    {
        $benchmark = new checkerBenchmarkDemo();
        $result = $this->object->add( $benchmark );
        $this->assertTrue( in_array( $benchmark, $this->object->suite ) );
    }

    /**
     * @group WebCheckerTests
     */
    public function testAdd_Key()
    {
        $benchmarks = array();
        $benchmarks['one'] = new checkerBenchmarkDemo();
        $benchmarks['two'] = new checkerBenchmarkUri();
        $result = $this->object->add( $benchmarks['one'], 'one' );
        $result = $this->object->add( $benchmarks['two'] );
        $this->assertTrue( in_array( $benchmarks['one'], $this->object->suite ) );
    }

    /**
     * @group WebCheckerTests
     */
    public function testRunFree()
    {
        $this->fillBenchmarkSuite();
        $this->object->run();
    }

    /**
     * @group WebCheckerTests
     */
    public function testRunWithOptions()
    {
        $this->fillBenchmarkSuite();
        $options = array();
        $options['excluded'] = array('checkerBenchmarkUri');
        $this->object->run($options);
    }

    protected function fillBenchmarkSuite()
    {
        $benchmarks = array();
        $benchmarks['one'] = new checkerBenchmarkDemo();
        $benchmarks['two'] = new checkerBenchmarkUri();
        $result = $this->object->add( $benchmarks['one'], 'one' );
        $result = $this->object->add( $benchmarks['two'] );
    }

}
?>
